#include "Level.h"

Level::Level()
{
}

Level::~Level()
{
}


void Level::init(float worldUnit)
{
	m_pointerToLevel = (int*)level1Layout;
	m_worldUnit = worldUnit;
}

void Level::setupLevel(std::vector<Ground*> &ground, std::vector<Obstacle*> &walls, std::vector<Sweeper*> &sweepers)
{
	for (int i = 0; i < maxCol; i++)
	{
		for (int j = 0; j < maxRow; j++)
		{
			if (m_pointerToLevel[maxRow * i + j] == 0 || m_pointerToLevel[maxRow * i + j] == 3)
			{
				ground.push_back(new Ground(sf::Vector2f(j * m_worldUnit, i * m_worldUnit)));
			}

			if (m_pointerToLevel[maxRow * i + j] == 1)
			{
				walls.push_back(new Obstacle(sf::Vector2f(j * m_worldUnit, i * m_worldUnit)));
			}

			if (m_pointerToLevel[maxRow * i + j] == 3)
			{
				sweepers.push_back(new Sweeper(sf::Vector2f(j * m_worldUnit, i * m_worldUnit)));
			}

			
		}
	}
}

